import { environment } from "src/environments/environment";
const base_url = environment.base_url;
export class Usuario {
  // Las variables opcionales (?) van al final
  constructor(nombre, email, passrowd, role, google, img, uid) {
    this.nombre = nombre;
    this.email = email;
    this.passrowd = passrowd;
    this.role = role;
    this.google = google;
    this.img = img;
    this.uid = uid;
  }
  get imagenUrl() {
    console.log(this);
    if (this.img) {
      return `${base_url}/upload/usuarios/${this.img}`;
    } else {
      return `${base_url}/upload/usuarios/no-image`;
    }
  }
}
